    private void AceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AceptarActionPerformed
        try{
            doc = txtdocumento.getText();
            boolean val = false;
            while (val == false) {
                Pattern p = Pattern.compile("[A-Z,a-z,&%$#@!()*^]");
                Matcher m = p.matcher(doc);
                if (m.find()) {
                    JOptionPane.showMessageDialog(null, "Por favor ingrese solo numeros");
                    return;
                } else {
                    val = true;
                }
            }
            txtdocumento.setText("");
            Registry r = LocateRegistry.getRegistry("127.0.0.1", 1234);
            ATM bank = (ATM) r.lookup("ATM");
            res = bank.verifcuenta(doc);
            if (res==0){
                JOptionPane.showMessageDialog(null,"Usted no posse cuentas, sera redirigido para crear una");
                Formulario fm = new Formulario(doc);
                fm.setVisible(true);
                this.setVisible(false);
            }if (res<3 && res>0){
                Autentificacion au = new Autentificacion(2);
                au.setVisible(true);
                this.setVisible(false);
            }if (res==3){
                JOptionPane.showMessageDialog(null,"Usted ya posse tres cuentas que es el limite permitido, sera redirigido al menu anterior");
                MenuB mb = new MenuB();
                mb.setVisible(true);
                this.setVisible(false);
            }
        }catch(Exception e){
            System.out.print(e.getMessage());
        }
    }//GEN-LAST:event_AceptarActionPerformed

